CREATE TABLE `collected_kanji` (
  `id` INTEGER PRIMARY KEY,
  `kanji` char(1) DEFAULT NULL,
  `repeatStatus` int(11) DEFAULT 0,
  `progress` int(11) DEFAULT 0,
  `record` int(11) DEFAULT 0,
  `autorepeat` tinyint(1) DEFAULT 0,
  `readings` varchar(100) DEFAULT NULL,
  `links` text DEFAULT NULL,
  `otherLinks` text DEFAULT NULL
);

