CREATE TABLE `jap_words` (
  `id` INTEGER PRIMARY KEY,
  `cardNumber` int(11) DEFAULT NULL,
  `learnStatus` int(11) DEFAULT -1,
  `fProgress` int(11) DEFAULT 0,
  `bProgress` int(11) DEFAULT 0,
  `fStats` int(11) DEFAULT 0,
  `fAutorepeat` tinyint(1) DEFAULT 0,
  `bStats` int(11) DEFAULT 0,
  `bAutorepeat` tinyint(1) DEFAULT 0,
  `altWriting` tinyint(1) DEFAULT 0,
  `writings` varchar(100) DEFAULT '',
  `rareWritings` varchar(100) DEFAULT '',
  `readings` varchar(100) DEFAULT '',
  `rareReadings` varchar(100) DEFAULT '',
  `translation` varchar(500) DEFAULT '',
  `example` varchar(500) DEFAULT ''
);

